import  React, { Component } from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import axios from 'axios';
import apiKey from '../config';
import Nav from './Nav';
import Search from './Search';
import PhotoPage from './PhotoPage';

class App extends Component {

  constructor() {
    super();
    this.state = {
      photos: [],
      loading: true
    }
  }

  componentDidMount() {
    this.searchPhotos();
  }

  searchPhotos = (query = "food") => {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${query}&per_page=24&format=json&nojsoncallback=1`)
    .then((response) => {
      this.setState({
        photos: response.data.photos.photo,
        loading: false
      })
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  render() {
    return (
      <BrowserRouter>
        <Switch>
          <div className="container">
            <Search onSearch={this.searchPhotos}/>
            <Nav />
            <Route path="/food" component={PhotoPage}/>
            <Route path="/beaches" component={PhotoPage}/>
            <Route path="/sunset" component={PhotoPage}/>
            {(this.state.loading) ? <p>Loading...</p> : <PhotoPage data={this.state.photos}/>}
          </div>
        </Switch>
      </BrowserRouter>
    );
  }
}

export default App;
