import  React, { Component } from 'react';
import Config from '../config';
import axios from 'axios';
import Nav from './Nav';
import Search from './Search';
import PhotoPage from './PhotoPage';
import apiKey from '../config';

const { api } = Config;

class App extends Component {

  constructor() {
    super();
    this.state = {
      photos: []
    }
  }

  componentDidMount() {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=travel%2C+food%2C+dogs&per_page=24&format=json&nojsoncallback=1`)
      .then((response) => {
        console.log(response)
        this.setState({
          photos: response.data.photos.photo
        })
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  searchPhotos = (query) => {
    axios.get(`https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&tags=${query}&per_page=24&format=json&nojsoncallback=1`)
    .then((response) => {
      console.log(response)
      this.setState({
        photos: response.data.photos.photo
      })
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  render() {
    console.log(this.state.photos)
    return (
      <div className="container">
        <Search onSearch={this.searchPhotos}/>
        <Nav />
        <PhotoPage data={this.state.photos} />
      </div>
    );
  }
}

export default App;
